async addToCart(id: string, name: string, price: number) {
  const cid =  await this.afs.createId()
  await this.afs.collection('carts').doc(cid)
.set({
 cid: cid,
 PROid: id,
 myDate: new Date().getTime(),
 Product_Name: name,
 price: price,
 quantity: 1,
}).then(()=>{
  this.router.navigateByUrl('/page')
})
}



 

